import queue

class Path:
        def __init__(self, r, c, cost, depth, TRI_HEIGHT):
            self.r = r
            self.c = c
            self.cost = cost
            self.depth = depth + 1
            self.th = TRI_HEIGHT
        
        def complete(self) -> bool:
            if self.depth == self.th:
                return True
            return False

class Solution:
    def minimumTotal(self, triangle: list[list[int]]) -> int:
        th = len(triangle)
        Q = queue.Queue()
        start = Path(0, 0, triangle[0][0], -1, th)
        solutions = []
        Q.put(start)
        while not Q.empty():
            p = Q.get()
            for i in range(2):
                chi = Path(p.r+1, p.c+i, p.cost + triangle[p.r+1][p.c+i], p.depth, th-1)
                if chi.complete():
                    solutions.append(chi)
                else:
                    Q.put(chi)
        min_path = solutions[0]
        for path in solutions:
            if path.cost < min_path.cost:
                min_path = path
        return min_path.cost
    
